const fetch=require("node-fetch"),cfg=require("../config");async function create(t,e=""){obj=Object.entries({url:t,costum:e}),conf=await cfg(),urls=new URLSearchParams(obj);let r=await fetch("https://"+conf["host-urlshort"]+"/api/v1/create?"+urls);if(200!==r.status)throw await r.json();let a=await r.json();return{status:r.status,craator:"Caliph",result:a.result}}module.exports=create.bind();